    return `<b style="color: blue"> 
    <b>Reading: ${consumption} kwh <b/>
    <br />
    <b> Status : Current reading is higher then thresholds(90 KWH)</b></b>`;
}
return `<b style="color: blue">
      <b>Reading: ${consumption} kwh <b/>
    <br />
  <b> Status: Normal </b>
  </b>`;

