descriptor.templateCss:
#container {
    overflow: auto;
}

.tbDatasource-container {
    margin: 5px;
    padding: 8px;
}

.tbDatasource-title {
    font-size: 1.200rem;
    font-weight: 500;
    padding-bottom: 10px;
}

.tbDatasource-table {
    width: 100%;
    box-shadow: 0 0 10px #ccc;
    border-collapse: collapse;
    white-space: nowrap;
    font-size: 1.000rem;
    color: #757575;
}

.tbDatasource-table td {
    position: relative;
    border-top: 1px solid rgba(0, 0, 0, 0.12);
    border-bottom: 1px solid rgba(0, 0, 0, 0.12);
    padding: 0px 18px;
    box-sizing: border-box;
}

descriptor.controllerScript:
var myChart;
var option;
var clearTimeOut;
self.onInit = function() {
    var chartDom = document.getElementById('main');
    myChart = echarts.init(chartDom);
option = {
  tooltip: {
    trigger: 'item'
  },
  legend: {
    top: '5%',
    left: 'center'
  },
  series: [
    {
      name: 'Access From',
      type: 'pie',
      radius: ['40%', '70%'],
      avoidLabelOverlap: false,
      label: {
        show: false,
        position: 'center'
      },
      emphasis: {
        label: {
          show: true,
          fontSize: 40,
          fontWeight: 'bold'
        }
      },
      labelLine: {
        show: false
      },
      data: [],
    }
  ]
};
    setTimeout(function() {
        myChart.setOption(option);
    }, 100);
