    background-color:#7870BE !important;
}

descriptor.controllerScript:
self.onInit = function() {
    self.ctx.$scope.progressBarWidget.onInit();
};

self.onDataUpdated = function() {
    let flotValues = [];
    console.log(self.ctx.data);
    for (var i = 0; i < self.ctx.data.length; i++) {
        var cellData = self.ctx.data[i];
                if (cellData.data.length > 0) {
            var decimals;
            if (typeof cellData.dataKey.decimals !== 'undefined' 
                && cellData.dataKey.decimals !== null ) {
                decimals = cellData.dataKey.decimals; 
            } else {
                decimals = self.ctx.decimals;
            }
            var tvPair = cellData.data[cellData.data.length - 1];
            var value = self.ctx.utils.formatValue(tvPair[1], decimals);
            flotValues.push(Number(value));
        }
    };
    console.log(flotValues);
    console.log(Math.min(...flotValues));
    console.log(Math.max(...flotValues));

    self.ctx.$scope.progressBarWidget.settings.tickMax = Math.max(...flotValues);
    self.ctx.$scope.progressBarWidget.value = Math.min(...flotValues);
    self.ctx.$scope.progressBarWidget.valueText = Math.min(...flotValues)+'';
    console.log(self.ctx.$scope.progressBarWidget);
    console.log(self.ctx.$scope);
    // console.log(flotValues);
    self.ctx.$scope.progressBarWidget.onDataUpdated();
};

self.typeParameters = function() {
    return {
        maxDatasources: 2,
        previewWidth: '280px',
        previewHeight: '180px',
        defaultDataKeysFunction: function() {
            return [{ name: 'pressure', label: 'Pressure', type: 'timeseries' }];
        }
    };
};


self.onDestroy = function() {
};
