let colors = ['#7870BE', '#7CD9FE', '#A760BD', '#F871AA', '#A9A4D5'];
self.onInit = function() {
    self.ctx.$scope.datasources = self.ctx
        .defaultSubscription.datasources;
     self.ctx.$scope.dataSourcescount = 
     self.ctx.$scope.datasources.length;
    self.ctx.$scope.data = self.ctx.defaultSubscription
        .data;
        self.ctx.$scope.colors = colors;

    self.ctx.$scope.datasourceData = [];
    var currentDatasource = null;
    var currentDatasourceIndex = -1;

    for (var i = 0; i < self.ctx.$scope.data
        .length; i++) {
        var dataKeyData = self.ctx.$scope.data[i];
        if (dataKeyData.datasource !=
            currentDatasource) {
            currentDatasource = dataKeyData.datasource
            currentDatasourceIndex++;
            self.ctx.$scope.datasourceData[
                currentDatasourceIndex] = [];

        }
        self.ctx.$scope.datasourceData[
            currentDatasourceIndex].push(
            dataKeyData);
    }
    self.ctx.updateWidgetParams();
}

self.onDataUpdated = function() {
    self.ctx.detectChanges();
}

self.onLatestDataUpdated = function() {
    // If you need to handle latest data updates, add your logic here
}

self.onResize = function() {
    // If you need to handle resize events, add your logic here
}

self.onEditModeChanged = function() {
    // If you need to handle edit mode changes, add your logic here
}

self.onDestroy = function() {
    // If you need to handle widget destruction, add your logic here
}

